@import url("https://fonts.googleapis.com/css2?family=Rajdhani:wght@600&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Sarpanch&display=swap");

.game__container {
  width: 293px;
  height: 395px;
  overflow-y: hidden;
  position: relative;
  font-family: "Rajdhani", sans-serif;
  box-shadow: 18px 4px 1px 4px rgba(0, 0, 0, 0.25) inset;
  border: 1px solid black;
  border-radius: 4px;
  & > img {
    width: 100%;
    height: 100%;
    display: block;
    object-fit: cover;
  }
  &__overlay {
    display: flex;
    flex-direction: column;
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    transition: transform 600ms;
    /*To create a transition effect, you must specify two things:
    the CSS property you want to add an effect to
    the duration of the effect*/
    transform: translate3d(0, 79%, 0); //x,y,z plane
    background-color: rgba(211, 211, 211, 0.2);
    border: 1px solid #2b2a2a;
    border-radius: 4px;
    box-sizing: border-box;
    backdrop-filter: blur(4px);
    box-shadow: 1px 0px 2px 1px rgba(0, 0, 0, 0.25) inset;
    &_heading-container {
      padding: 0.8rem;
      height: 8em;
      h4 {
        margin: 0;
        transition: transform 300ms;
        // transform: translate3d(0, 100%, 0);
        font-size: 4rem;
        color: rgb(252, 248, 7);
        text-align: center;
        text-shadow: 3px 3px 0px rgb(7, 7, 7);
        font-family: "Sarpanch", sans-serif;
      }
      hr {
        height: 4px;
        background-color: black;
        border: none;
        display: none;
      }
    }

    &-info-container {
      padding: 1rem;
      width: 96%;
      margin: 0 auto;
      backdrop-filter: blur(34px);
      transition: opacity 600s, transform 100ms;
      border: 0.2px solid rgb(70, 69, 69);
      border-radius: 4px;
      height: 60%;
      font-size: 1.5em;
      line-height: 1.7;
    }
    .game__votingbar-container {
      height: 9em;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      &-bar {
        border: 1px solid #868584;
        border-radius: 10em;
        text-align: center;
        position: relative;
        color: white;
        background-color: rgb(15, 15, 15);
        &-text-exceptional {
          letter-spacing: 0.7em;
          position: fixed;
          left: 15%;
          z-index: 20;
        }
        &-text-recommended {
          letter-spacing: 0.7em;
          position: fixed;
          left: 15%;
          z-index: 20;
        }
        &-text-not-bad {
          letter-spacing: 0.3em;
          z-index: 20;
          position: fixed;
          left: 35%;
        }
        &-text-dont-bother {
          letter-spacing: 0.3em;
          z-index: 20;
          position: fixed;
          left: 25%;
        }

        &-color {
          background-color: rgb(26, 184, 26);
          height: 27px;
          // opacity: 0.5;
          position: relative;
          z-index: 10;
          border-radius: 10em 0 0 10em;
          width: 50%;
          text-align: center;
        }
      }

      &-bar-rate-container {
        width: 2.5em;
        height: 2.1em;
        border-radius: 50%;
        border: 2px solid rgb(0, 0, 0);
        background-color: rgba(179, 238, 168, 0.863);
        color: rgb(0, 0, 0);
        position: absolute;
        z-index: 40;
        @include flexedCentered;
        right: -0.5em;
        top: -0.4em;
        display: none;
        // transition: display 800ms 4s;
        // transition: all 1s ease-in 4s;
      }
    }

    &-platforms-icon-container {
      display: flex;
      justify-content: flex-end;
      align-items: center;
      height: 8em;

      &-platforms {
        background-color: rgb(0, 0, 0);
        border-radius: 50%;
        height: 50px;
        width: 50px;
        display: flex;
        justify-content: center;
        align-items: center;
        margin-right: 0.7em;
        img {
          width: 40px;
          height: 30px;
          filter: sepia(100%) invert(100%) brightness(200%);
        }
      }
    }
  }

  a {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;

    &:hover,
    &:focus {
      ~ .game__container__overlay {
        transform: translate3d(0, 0, 0);

        &-game-title {
          transform: translate3d(0, 0, 0);
        }
        hr {
          display: block;
        }
        &-info-container {
          opacity: 1;
        }
        .game__votingbar-container-bar-rate-container {
          display: inline-block;
        }
      }
    }
  }
}

