@import "config";
@import "variables";
.sidebar {
  border: 1px solid black;
  background-color: $tertiary-color;
  position: relative;
  font-family: "Quantico", sans-serif;

  > section {
    background-color: $quaternery-color;
    border-radius: 0.3em;
  }
  &__filter-container {
    border: 2px solid rgb(150, 149, 149);
    // height: 12.75em;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    // @include centered;
    padding: 2em;
    label {
      font-size: 1.5em;
      font-weight: bold;
    }
  }

  &__filter-option {
    background-color: $color-grey-light-1;
    border: 2px solid black;
    color: rgb(34, 33, 33);
    font-family: "Quantico", sans-serif;
  }

  &__browseby-container {
    border: 2px solid rgb(150, 149, 149);

    position: relative;
    @include centeredColumn;
    &-heading {
      // background-color: lawngreen;
      // border-radius: 0.3em;
      width: 90%;
    }
    &_btns {
      background-color: #0b0a0a;
      border-radius: 0.3em;
      // @include centeredSpaceBetween;
      @include centeredSpaceAround;
      // @include centeredColumn;
      flex-direction: column;
      width: 90%;
      height: 90%;
      padding: 1em 0;

      &-btn {
        padding: 0.6em 1em;
        width: 70%;
        border-radius: 10px;
        font-family: "Quantico", sans-serif;
        background-color: $color-grey-light-1;
      }
    }
  }

  footer {
    background-color: $quaternery-color;
    position: absolute;
    bottom: 0;
    height: 24%;
    width: 100%;
    // @include flexedCentered;
    display: flex;
    justify-content: center;
    p {
      position: relative;
      margin: 0 auto;
      top: 10%;
    }
  }
}

